import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import Validation from './SignupValidation';
import axios from 'axios'

function Signup(){
    const [values,setValues]=useState({
        firstname:'',
        lastname:'',
        email: '',
        password:''
    })
    const [errors, setErrors]=useState({})

    const handleInput=(event)=>{
    setValues(prev=>({...prev,[event.target.name]:[event.target.value]}))
    
    }
    const handleSubmit=(event)=>{
        event.preventDefault();
        const err=Validation(values);
        setErrors(err);
        if(err.firstname===''&&err.lastname===''&&err.email===''&&err.password===''){
            axios.post('http://localhost:8081/signup',values)
            .then(res=>console.log(res))
            .catch(err=>console.log(err));
        }
    }


    return(
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
            <h2>Sign up</h2>
            <form action='' onSubmit={handleSubmit}>
            <div className='mb-3'>
            <label htmlFor='first name'><strong>First name</strong></label>
        <input type='first name' placeholder='First name' name='firstname'
        onChange={handleInput} className='form-control rounded-0'/>
                {errors.firstname && <span className='text-danger'>{errors.firstname}</span>}

            </div>  
            <div className='mb-3'>
            <label htmlFor='last name'><strong>Last name</strong></label>
        <input type='last name' placeholder='Last name' name='lastname'
        onChange={handleInput} className='form-control rounded-0'/>
              {errors.lastname && <span className='text-danger'>{errors.lastname}</span>}

            </div>  
            <div className='mb-3'>
            <label htmlFor='email'><strong>Email</strong></label>
        <input type='email' placeholder='Enter Email' name='email'
        onChange={handleInput} className='form-control rounded-0'/>
        {errors.email && <span className='text-danger'>{errors.email}</span>}

            </div>  
            <div className='mb-3'>
            <label htmlFor='password'><strong>Password</strong></label>
        <input type='password' placeholder='Enter Password' name='password'
        onChange={handleInput} className='form-control rounded-0'/>
        {errors.password && <span className='text-danger'>{errors.password}</span>}

            </div>  
            <button type='submit' className='btn btn-success w-100 rounded-0'>Sign up</button>
            <p></p>
            <Link to='/' className='btn btn-defualt border w-100 bg-light rounded-0 text-decoration-none'>Already have an account? Sign in</Link>

            
            </form>
        </div>
    </div>

    )
}


export default Signup
