import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const PrivateRoute = ({ component: Component, ...rest }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(null);

    useEffect(() => {
        // Call the server to check authentication status
        axios.get('http://localhost:8081/check-auth', { withCredentials: true })
            .then((res) => {
                if (res.status === 200) {
                    setIsAuthenticated(true); // User is authenticated
                }
            })
            .catch(() => {
                setIsAuthenticated(false); // User is not authenticated
            });
    }, []);

    if (isAuthenticated === null) {
        // Show a loading spinner or similar while checking authentication
        return <div>Loading...</div>;
    }

    return (
        isAuthenticated ? <Component {...rest} /> : <Navigate to="/login" />
    );
};

export default PrivateRoute;
