import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './style.css';

function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isVerified, setIsVerified] = useState(false); // Track if email is verified
    const navigate = useNavigate();

    // Function to verify if the email exists in the database
    const handleVerifyEmail = (event) => {
        event.preventDefault();
        setMessage(''); // Clear any previous messages

        axios.post('http://localhost:8081/verify-email', { email })
            .then(res => {
                if (res.data.message==="Email sent successfuly.") {
                    setMessage('Check your email for the code to reset your password.');
                    setIsVerified(true);  // Allow user to proceed to password reset
                } else {
                    setMessage('Email not found.');
                    setIsVerified(false); // Block password reset if email doesn't exist
                }
            })
            .catch(err => {
                console.error('Verification Error:', err.response?.data || err);
                setMessage('Error verifying email.');
            });
    };

    // Function to reset the password
    const handleSubmit = (event) => {
        event.preventDefault();
        if (!isVerified) {
            setMessage('Please verify your email first.');
            return;
        }

        if (password !== confirmPassword) {
            setMessage("Passwords don't match!");
        } else {
            axios.post('http://localhost:8081/reset-password', { email, password })
                .then(res => {
                    setMessage('Password reset successfully! Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000); // Redirect to login page after 2 seconds
                })
                .catch(err => setMessage('Error resetting password.'));
        }
    };

    return (
        <div>
            <Helmet>
                <title>Forgot Password</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className='container'>
                <h2>Forgot Password</h2>

                {/* Email Verification Form */}
                {!isVerified && (
                    <form onSubmit={handleVerifyEmail}>
                        <div className='mb-3'>
                            <label htmlFor='email'><strong>Email</strong></label>
                            <input
                                type='email'
                                placeholder='Enter your email'
                                name='email'
                                className="form-control rounded-0"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        {message && <div className="alert alert-info">{message}</div>}
                        <button type='submit' className='btn btn-primary w-100 rounded-0'>Verify Email</button>
                    </form>
                )}

                {/* Password Reset Form */}
                {isVerified && (
                    <form onSubmit={handleSubmit}>
                        <div className='mb-3'>
                            <label htmlFor='password'><strong>New Password</strong></label>
                            <input
                                type='password'
                                placeholder='Enter new password'
                                name='password'
                                className="form-control rounded-0"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className='mb-3'>
                            <label htmlFor='confirmPassword'><strong>Confirm New Password</strong></label>
                            <input
                                type='password'
                                placeholder='Confirm new password'
                                name='confirmPassword'
                                className="form-control rounded-0"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                required
                            />
                        </div>
                        {message && <div className="alert alert-info">{message}</div>}
                        <button type='submit' className='btn btn-primary w-100 rounded-0'>Reset Password</button>
                    </form>
                )}

                <Link to='/' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                    Back to Login
                </Link>
            </div>
        </div>
    );
}

export default ForgotPassword;
