import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './style.css';

function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        setMessage('');
        axios.post('http://localhost:8081/forgot-password', { email })
            .then((response) => {
                if (response.data === 'Email Sent') {
                    setMessage('Token sent to your email. Redirecting...');
                    setTimeout(() => navigate('/verify-token'), 2000); // Navigate to token verification
                } else {
                    setMessage('Email not found.');
                }
            })
            .catch(() => setMessage('Error sending email.'));
    };

    return (
        <div>
            <Helmet>
                <title>Forgot Password</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className='container'>
                <h2>Forgot Password</h2>
                <form onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor='email'><strong>Email</strong></label>
                        <input
                            type='email'
                            placeholder='Enter your email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    {message && <div className="alert alert-info">{message}</div>}
                    <button type='submit' className='btn btn-primary w-100'>Send Token</button>
                    <Link to='/login' className='mt-3 btn btn-default border w-100 bg-light'>Back to Login</Link>
                </form>
            </div>
        </div>
    );
}

export default ForgotPassword;
