import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Helmet } from 'react-helmet';
import './style.css';

function AddCustomer() {
    const [customer, setCustomer] = useState({ name: '', address: '', id: '' });
    const [message, setMessage] = useState('');
    const [customers, setCustomers] = useState([]); // State to store all customers
    const navigate = useNavigate();

    const handleInput = (event) => {
        setCustomer({ ...customer, [event.target.name]: event.target.value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        axios.post('http://localhost:8081/add-customer', customer, { withCredentials: true })
            .then(res => {
                setMessage('Customer added successfully!');
                fetchCustomers(); // Refresh customer list after adding a new one
                setCustomer({ name: '', address: '', id: '' });
            })
            .catch(err => {
                if (err.response) {
                    if (err.response.status === 401) {
                        setMessage('Unauthorized access. Please log in.');
                        navigate('/');
                    } else {
                        setMessage(err.response.data || 'Error adding customer. Please try again later.');
                    }
                } else {
                    setMessage('A problem occurred.');
                }
            });
    };

    // Function to fetch all customers
    const fetchCustomers = () => {
        axios.get('http://localhost:8081/get-customers', { withCredentials: true })
            .then(res => {
                setCustomers(res.data); // Assuming the response is an array of customers
            })
            .catch(err => {
                console.error("Error fetching customers:", err);
            });
    };

    // Function to delete a customer
    const handleDelete = (id) => {
        axios.delete(`http://localhost:8081/delete-customer/${id}`, { withCredentials: true })
            .then(res => {
                setMessage('Customer deleted successfully!');
                fetchCustomers(); // Refresh customer list after deletion
            })
            .catch(err => {
                console.error("Error deleting customer:", err);
                setMessage('Error deleting customer. Please try again later.');
            });
    };

    // Fetch customers on component mount
    useEffect(() => {
        fetchCustomers();
    }, []);

    const handleLogout = () => {
        axios.post('http://localhost:8081/logout', {}, { withCredentials: true })
            .then(() => {
                navigate('/');
            })
            .catch(err => {
                console.error("Logout failed:", err);
                setMessage('Logout failed. Please try again later.');
            });
    };

    return (
        <div>
            <Helmet>
                <title>Add Customer</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
                <div className='user-management'>
                    <div className='user-icon-container'>
                        <img src="/user-icon.png" alt="User Icon" className="user-icon" />
                        <div className="dropdown-content">
                            <Link to="/verify-password">Change Password</Link>
                            <Link to="#" onClick={handleLogout}>Log Out</Link>
                        </div>
                    </div>
                </div>
            </header>

            <div className="container mt-3">
                <h2>Add New Customer</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="name">Name</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            value={customer.name}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="address">Address</label>
                        <input
                            type="text"
                            name="address"
                            className="form-control"
                            value={customer.address}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="id">ID</label>
                        <input
                            type="text"
                            name="id"
                            className="form-control"
                            value={customer.id}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    {message && <div className="alert alert-info">{message}</div>}
                    <button type="submit">Add</button>
                    <Link to='/home' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                        Back to Home Page
                    </Link>
                </form>

                <h2 className="mt-4">All Customers</h2>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {customers.map(cust => (
                            <tr key={cust.id}>
                                <td>{cust.id}</td>
                                <td>{cust.name}</td>
                                <td>{cust.address}</td>
                                <td>
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => handleDelete(cust.id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default AddCustomer;
