import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Helmet } from 'react-helmet';
import './style.css';

function Home() {
    const [customers, setCustomers] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        axios.get('http://localhost:8081/customers')
            .then(res => {
                setCustomers(res.data);
            })
            .catch(err => console.log(err));
    }, []);

    const handleDelete = (id) => {
        axios.delete(`http://localhost:8081/delete-customer/${id}`)
            .then(res => {
                setMessage('Customer deleted successfully!');
                setCustomers(customers.filter(customer => customer.id !== id));
            })
            .catch(err => {
                if (err.response && err.response.data) {
                    setMessage(err.response.data); // Display the specific error message from the server
                } else {
                    setMessage('A problem occurred.');
                }
            });
    };
    return (
        <div>
            <Helmet>
                <title>Home</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className="container">
        
                <h2>Customers</h2>
                {message && <div className="alert alert-info">{message}</div>}
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Address</th>
                        </tr>
                    </thead>
                    <tbody>
                        {customers.map(customer => (
                            <tr key={customer.id}>
                                <td>{customer.id}</td>
                                <td>{customer.name}</td>
                                <td>{customer.address}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className='center'>
                <Link to='/add-customer' className="btn btn-primary">Add Customer</Link>
            </div>
            </div>
            
        </div>
    );
}

export default Home;
