import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import axios from 'axios';
import './style.css';
import { isPasswordValid, addPasswordToHistory } from './passwordValidation';

function Signup() {
    const [values, setValues] = useState({
        firstname: '',
        lastname: '',
        email: '',
        password: ''
    });

    const navigate = useNavigate();
    const [errors, setErrors] = useState({});
    const [serverMessage, setServerMessage] = useState(''); // New state to handle server message

    const handleInput = (event) => {
        setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        // Basic client-side validation
        const validationErrors = await validateForm(values);
        setErrors(validationErrors);

        if (Object.keys(validationErrors).length === 0) {
            try {
                const response = await axios.post('http://localhost:8081/signup', values);
                setServerMessage(''); // Clear server message on success
                navigate('/'); // Redirect to login page
            } catch (error) {
                if (error.response && error.response.status === 409) {
                    setServerMessage('User with this email already exists');
                } else {
                    setServerMessage('An error occurred during registration');
                }
            }
        }
    };

     // Enhanced password validation
     const validateForm = async (values) => {
        const errors = {};

        if (!values.firstname) errors.firstname = "First name is required";
        if (!values.lastname) errors.lastname = "Last name is required";
        if (!values.email) errors.email = "Email is required";

        if (!values.password) {
            errors.password = "Password is required";
        } else {
            const passwordErrors = await isPasswordValid(values.password);
            if (!passwordErrors.isValid) {
                errors.password = passwordErrors.message;
            }
        }

        return errors;
    };
    return (
        <div>
            <Helmet>
                <title>Register</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className='container'>
                <h2>Sign up</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor='firstname'><strong>First name</strong></label>
                        <input
                            type='text'
                            placeholder='First name'
                            name='firstname'
                            onChange={handleInput}
                        />
                        {errors.firstname && <span className='text-danger'>{errors.firstname}</span>}
                    </div>
                    <div>
                        <label htmlFor='lastname'><strong>Last name</strong></label>
                        <input
                            type='text'
                            placeholder='Last name'
                            name='lastname'
                            onChange={handleInput}
                        />
                        {errors.lastname && <span className='text-danger'>{errors.lastname}</span>}
                    </div>
                    <div>
                        <label htmlFor='email'><strong>Email</strong></label>
                        <input
                            type='email'
                            placeholder='Enter Email'
                            name='email'
                            onChange={handleInput}
                        />
                        {errors.email && <span className='text-danger'>{errors.email}</span>}
                        {serverMessage && <p className='text-danger'>{serverMessage}</p>}
                    </div>
                    <div className="password-container">
                        <label htmlFor='password'><strong>Password</strong></label>
                        <input
                            type='password'
                            placeholder='Enter Password'
                            name='password'
                            onChange={handleInput}
                        />
                        {errors.password && <span className='text-danger'>{errors.password}</span>}
                        <div className="password-tooltip">
                            <ul>
                                <li>At least 8 characters</li>
                                <li>Include both upper and lower case letters</li>
                                <li>Include at least one number</li>
                                <li>Include at least one special character</li>
                            </ul>
                        </div>
                    </div>
                    
                    <button className='mt-3' type='submit'>Sign up</button>
                </form>
                <p>
                    <Link to='/' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                        Sign in
                    </Link>
                </p>
            </div>
        </div>
    );
}

export default Signup;
