import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import axios from 'axios';
import './style.css';
import { Helmet } from 'react-helmet';


function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [isVerified, setIsVerified] = useState(false);


    const handleInputChange = (event) => {
        setEmail(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setMessage(''); // Clear previous messages
        

        axios.post('http://localhost:8081/forgot-password', { email })
            .then((response) => {
                if (response.data === 'Email Sent') {
                    setMessage('Password reset link has been sent to your email.');
                } else if (response.data === 'Email Not Found') {
                    setMessage('Email not found.');
                }
            })
            .catch((err) => {
                console.log(err);
                setMessage('An error occurred. Please try again later.');
            });
    };

    return (
        <div>
             <Helmet>
                <title>Log in</title>
            </Helmet>
            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className='container'>
                <h2>Forgot Password</h2>
                <form onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor='email'><strong>Email</strong></label>
                        <input
                            type='email'
                            placeholder='Enter Email'
                            name='email'
                            value={email}
                            onChange={handleInputChange}
                            
                        />
                        {message && <div className="alert alert-info">{message}</div>}
                    </div>
                    <button type='submit' className='btn btn-primary w-100 rounded-0'>Send</button>
                    <Link to='/' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                        Sign in
                    </Link>
                </form>
            </div>
        </div>
    );
}

export default ForgotPassword;
