import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './style.css';

function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [code, setCode] = useState('');
    const [isCodeSent, setIsCodeSent] = useState(false); // To track if the code has been sent
    const [message, setMessage] = useState('');
    const [isVerified, setIsVerified] = useState(false); // Track if email is verified
    const navigate = useNavigate();

    // Function to verify if the email exists in the database
    const handleEmailSubmit  = (event) => {
        event.preventDefault();
        setMessage(''); // Clear any previous messages

        // axios.post('http://localhost:8081/verify-email', { email })
        //     .then(res => {
        //         if (res.data.message==="Email sent successfuly.") {
        //             setMessage('Check your email for the code to reset your password.');
        //             setIsVerified(true);  // Allow user to proceed to password reset
        //         } else {
        //             setMessage('Email not found.');
        //             setIsVerified(false); // Block password reset if email doesn't exist
        //         }
        //     })
        //     .catch(err => {
        //         console.error('Verification Error:', err.response?.data || err);
        //         setMessage('Error verifying email.');
        //     });
        axios.post('http://localhost:8081/send-code', { email })
        .then((response) => {
            if (response.data.success) {
                setMessage('A verification code has been sent to your email.');
                setIsCodeSent(true); // Now the code is sent, user can enter the code
            } else {
                setMessage('Email not found.');
            }
        })
        .catch((err) => {
            console.log(err);
            setMessage('An error occurred. Please try again later.');
        });
    };

    // Function to reset the password
    // const handleSubmit = (event) => {
    //     event.preventDefault();
    //     if (!isVerified) {
    //         setMessage('Please verify your email first.');
    //         return;
    //     }

    //     if (password !== confirmPassword) {
    //         setMessage("Passwords don't match!");
    //     } else {
    //         axios.post('http://localhost:8081/reset-password', { email, password })
    //             .then(res => {
    //                 setMessage('Password reset successfully! Redirecting to login...');
    //                 setTimeout(() => navigate('/'), 2000); // Redirect to login page after 2 seconds
    //             })
    //             .catch(err => setMessage('Error resetting password.'));
    //     }
    // };
     // Verify the code entered by the user
     const handleCodeSubmit = (event) => {
        event.preventDefault();
        setMessage(''); // Clear previous messages

        // Verify the code with the backend
        axios.post('http://localhost:8081/verify-code', { email, code })
            .then((response) => {
                if (response.data.success) {
                    setMessage('Code verified successfully!');
                    setIsVerified(true);
                    navigate('/change-password'); // Redirect to change password page
                } else {
                    setMessage('Incorrect code or code expired.');
                }
            })
            .catch((err) => {
                console.log(err);
                setMessage('An error occurred. Please try again later.');
            });
    };

    return (
        <div>
            <Helmet>
                <title>Forgot Password</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className='container'>
                <h2>Forgot Password</h2>

                {/* Email Verification Form */}
                {!isCodeSent  && (
                    <form onSubmit={handleEmailSubmit}>
                        <div className='mb-3'>
                            <label htmlFor='email'><strong>Email</strong></label>
                            <input
                                type='email'
                                placeholder='Enter your email'
                                name='email'
                                className="form-control rounded-0"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        {message && <div className="alert alert-info">{message}</div>}
                        <button type='submit' className='btn btn-primary w-100 rounded-0'>Verify Email</button>
                    </form>
                )}

                {/* Code verifying Form */}
                {isCodeSent &&!isVerified &&  (
                    <form onSubmit={handleCodeSubmit}>
                        <div className='mb-3'>
                            <label htmlFor='code'><strong>Enter verification code</strong></label>
                            <input
                                type='text'
                                placeholder='Enter 6-digit code'
                                name='code'
                                className="form-control rounded-0"
                                onChange={(e) => setCode(e.target.value)}
                                value={code}
                                required
                            />
                        </div>
                        
                        {message && <div className="alert alert-info">{message}</div>}
                        <button type='submit' className='btn btn-primary w-100 rounded-0'>Verify code</button>
                    </form>
                )}

                <Link to='/' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                    Back to Login
                </Link>
            </div>
        </div>
    );
}

export default ForgotPassword;
