import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './style.css';

function ChangePassword() {
    const [currentPassword, setCurrentPassword] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isVerified, setIsVerified] = useState(false); // To track if current password is verified
    const navigate = useNavigate();

    // Function to verify the current password
    const handleVerifyPassword = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8081/verify-password', { currentPassword }, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}` // Pass token from localStorage
            }
        })
        .then(res => {
            console.log('Verification Response:', res.data); 
            if (res.data.match) { // Assuming your backend returns { match: true } if passwords match
                setMessage('Password verified successfully!');
                setIsVerified(true); // Allow the password change form
            } else {
                setMessage('Current password is incorrect.');
                setIsVerified(false);
            }
        })
        .catch(err => {
            console.error('Verification Error:', err.response.data);
            setMessage('Error verifying password.')});
    };

    // Function to change the password
    const handleSubmit = (event) => {
        event.preventDefault();
        if (!isVerified) {
            setMessage('Please verify your current password first.');
            return;
        }

        if (password !== confirmPassword) {
            setMessage("Passwords don't match!");
        } else {
            axios.post('http://localhost:8081/change-password', { password }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}` // Send the token
                }
            })
            .then(res => {
                setMessage('Password changed successfully!');
                setTimeout(() => navigate('/'), 2000);
            })
            .catch(err => setMessage('Error changing password.'));
        }
    };

    return (
        <div>
            <Helmet>
                <title>Change Password</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>

            <div className="container">
                <h2>Change Password</h2>

                {/* Verify current password */}
                {!isVerified && (
                    <form onSubmit={handleVerifyPassword}>
                        <div className="mb-3">
                            <label htmlFor="current-password">Current Password</label>
                            <input
                                type="password"
                                name="currentPassword"
                                className="form-control"
                                value={currentPassword}
                                onChange={e => setCurrentPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit">Verify Current Password</button>
                    </form>
                )}

                {/* Change password form */}
                {isVerified && (
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="password">New Password</label>
                            <input
                                type="password"
                                name="password"
                                className="form-control"
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="confirm-password">Confirm New Password</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                className="form-control"
                                value={confirmPassword}
                                onChange={e => setConfirmPassword(e.target.value)}
                                required
                            />
                        </div>
                        {message && <p>{message}</p>}
                        <button type="submit">Change Password</button>
                    </form>
                )}

                {message && <p>{message}</p>}
                
                <Link to='/home' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                    Back to Home Page
                </Link>
            </div>
        </div>
    );
}

export default ChangePassword;
