import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Helmet } from 'react-helmet';
import './style.css';

function AddCustomer() {
    const [customer, setCustomer] = useState({ name: '', address: '', id: ''});
    const [message, setMessage] = useState('');
   

    const handleInput = (event) => {
        setCustomer({ ...customer, [event.target.name]: event.target.value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8081/add-customer', customer)
            .then(res => {
                setMessage('Customer added successfully!');
            })
            .catch(err => {
                setMessage('A problem occurred.');
            });
    };

    return (
        <div>
            <Helmet>
                <title>Add customer</title>
            </Helmet>

            <header>
                <h1>Communication LTD</h1>
            </header>
            <div className="container mt-3">
                <h2>Add New Customer</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="name">Name</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            value={customer.name}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="address">Address</label>
                        <input
                            type="text"
                            name="address"
                            className="form-control"
                            value={customer.address}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="id">Id</label>
                        <input
                            type="text"
                            name="id"
                            className="form-control"
                            value={customer.id}
                            onChange={handleInput}
                            required
                        />
                    </div>
                    {message && <div className="alert alert-info">{message}</div>}
                    <button type="submit">Add</button>
                    <Link to='/home' className='mt-3 btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>
                    Back to Home Page
                    </Link>
                </form>
            </div>
        </div>
    );
}

export default AddCustomer;
